import React from 'react';
import styled from 'styled-components'
const Modal= () => {
    return(
        <>
            <Overlay>
                <ContenedorModal></ContenedorModal>
            </Overlay>
        </>
    );

}
export default Modal;
const Overlay = styled.div`
width: 100vw;
height: 100vh;
position: fixed;
top: 0;
left: 0;
background : rgba(0,0,0,0.5);
padding:40px
display :flex;
align-items: center;
justify-component: center;

`;
const ContenedorModal = styled.div`
with: 500px
min-height: 100px;
background :#fff;
position: relative;
border-radius:8px;
box-shadow: rgba(100,100,111,0.2) 0px 7px 29px 0px;
padding 20px;
`;
